import React, { useEffect, useState } from "react";
import { Alert } from "react-native";
import { createStackNavigator } from "@react-navigation/stack";
import AsyncStorage from "@react-native-async-storage/async-storage";
import { useDispatch } from "react-redux";
import jwt_decode from "jwt-decode";

import { setAuth } from "../../contexts/slices/authSlice";

import { getCurrentTimeStamp } from "../../utils/date";
import { expireTime } from "../../configs/token";

import Login from "../Login";
import Menu from "../Menu";
import CheckStock from "../CheckStock";

const getTimeFromToken = (token: string): number => {
  const { time }: any = token ? jwt_decode(token) : 0;
  return time;
};

const Main: React.FC = () => {
  const Stack: any = createStackNavigator();

  const dispatch = useDispatch();
  const [isLogin, setIsLogin] = useState<boolean>(false);

  const validateToken = async () => {
    try {
      //? get token, status from AsyncStorage
      const auth: any = (await AsyncStorage.getItem("auth")) || {};
      const accessStatus: any =
        (await AsyncStorage.getItem("accessStatus")) || false;
      const accessToken: any =
        (await AsyncStorage.getItem("accessToken")) || "";
      console.log(accessStatus);
      if (JSON.parse(accessStatus)) {
        const accessTime = getTimeFromToken(String(accessToken));
        const currentTime = getCurrentTimeStamp();

        //? if token is expired
        if (currentTime - accessTime > expireTime) {
          Alert.alert("Alert", "Your token is expired, please login again", [
            {
              text: "OK",
              onPress: () => {
                dispatch(setAuth({}));
                setIsLogin(false);
              },
            },
          ]);
        }

        //? if token is valid
        dispatch(setAuth(JSON.parse(auth)));
        setIsLogin(true);
      } else {
        dispatch(setAuth({}));
        setIsLogin(false);
      }
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    validateToken();
  }, []);

  return (
    <>
      <Stack.Navigator>
        {!isLogin && <Stack.Screen name="Login" component={Login} />}
        <Stack.Screen name="Menu" component={Menu} />
        <Stack.Screen name="CheckStock" component={CheckStock} />
      </Stack.Navigator>
    </>
  );
};

export default Main;
