/**
 * Token Expire Time
 * ----------------------
 * ( 1 Day ) : 60 * 60 * 24 = 86400
 * ( 1 Hour ) : 60 * 60     = 3600
 */

export const expireTime = 60 * 60 * 24; // 1 Day (seconds)

export const refreshTime = 60 * 60 * 2; // 2 hour (seconds)

export const idleTime = 1000 * 60 * 60; // 1 hour (milliseconds)

export const confirmTime = 1000 * 60 * 5; // 5 minutes (milliseconds)
