import React, { ReactNode, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Alert } from "react-native";
import { createStackNavigator } from "@react-navigation/stack";
import AsyncStorage from "@react-native-async-storage/async-storage";

import { setAuth, selectAuth } from "../../contexts/slices/authSlice";

import { getCurrentTimeStamp } from "../../utils/date";
import { expireTime } from "../../configs/token";
import { getTimeFromToken } from "../../utils/token";
import { ReactInstance } from "react";

const Login = React.lazy(() => import("../Login"));
const Menu = React.lazy(() => import("../Menu"));

const Main: React.FC = () => {

  let views: ReactNode[] = [];

  const Stack: any = createStackNavigator();
  const dispatch = useDispatch();
  const { authResult } = useSelector(selectAuth);

  let test : any[] = authResult?.data?.permission.map((value:any)=>{

 console.log(value)

  })



  const validateToken = async () => {
    try {

      //? get token, status from AsyncStorage
      const auth: any = JSON.parse(await AsyncStorage.getItem("auth") || "{}");

      if (auth.status) {

        const accessTime = getTimeFromToken(auth.data.token);
        const currentTime = getCurrentTimeStamp();

        //? if token is expired
        if (currentTime - accessTime > expireTime) {
          Alert.alert("Alert", "Your token is expired, please login again", [
            {
              text: "OK",
              onPress: () => {
                dispatch(setAuth({}));
              },
            },
          ]);
        }

        //? if token is valid
        dispatch(setAuth(auth));

      } else {
        dispatch(setAuth({}));
      }
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    validateToken();
  }, []);

  return (
    <>
      <Stack.Navigator>
        {authResult?.status ? (
          <>
            <Stack.Screen name="Menu" component={Menu} />

          </>
        ) : (
          <Stack.Screen name="Login" component={Login}  />
        )}
      </Stack.Navigator>
    </>
  );
};

export default Main;
